
USE w3db;

DROP TABLE IF EXISTS Customers;

CREATE TABLE Customers (
                           CustomerID INT AUTO_INCREMENT PRIMARY KEY,
                           CustomerName VARCHAR(100),
                           ContactName VARCHAR(100),
                           Address VARCHAR(100),
                           City VARCHAR(50),
                           PostalCode VARCHAR(20),
                           Country VARCHAR(50)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

