const items = $input.all();

const positiveKeywords = ['감사', '추천', '좋아요', '최고', '만족', '편리', '굿', '짱', '대박', '사랑', '훌륭', '완벽'];
const csRiskKeywords = ['환불', '교환', '파손', '불량', '최악', '문제'];

const filtered = items.filter(item => {
  const content = item.json.review_content || '';
  const hasRisk = csRiskKeywords.some(word => content.includes(word));
  if (hasRisk) return false;

  const hasPositive = positiveKeywords.some(word => content.includes(word));
  return hasPositive;
});

if (filtered.length === 0) {
  return items.slice(0, 10);
}

return filtered;

const items = $input.all();

const positiveKeywords = ['감사', '추천', '좋아요', '최고', '만족', '편리', '굿', '짱', '대박', '사랑', '훌륭', '완벽', '고마워', '고맙', '따뜻', '감동', '행복', '강추', '대만족', '멋져', '좋네'];

const scored = items.map(item => {
  const content = item.json.review_content || '';
  let score = 0;
  positiveKeywords.forEach(kw => {
    const regex = new RegExp(kw, 'g');
    const matches = content.match(regex);
    if (matches) score += matches.length * 10;
  });
  score += (item.json.rating || 0) * 5;
  score = Math.min(score, 100);
  return {
    json: {
      ...item.json,
      positive_score: score
    }
  };
});

scored.sort((a, b) => b.json.positive_score - a.json.positive_score);

const top5 = scored.slice(0, Math.min(5, scored.length));
const selected = top5[Math.floor(Math.random() * top5.length)];

return [selected];

const item = $input.all()[0];
const prevData = $('Score_And_Select').first().json;

const fullText = item.json.content?.[0]?.text || '';
const formattedMessage = fullText;
const commentary = '';

return [{
  json: {
    review_id: prevData.review_id || 'UNKNOWN',
    id: prevData.id || 0,
    review_content: prevData.review_content || '',
    formatted_message: formattedMessage,
    commentary: commentary
  }
}];
