const items = $input.all();

const totalCount = items.reduce((sum, item) => sum + (item.json.cnt || 0), 0);

const stats = items.map(item => ({
  category: item.json.product_category,
  count: item.json.cnt,
  avg_rating: item.json.avg_rating,
  avg_length: item.json.avg_len
}));

return [{
  json: {
    summary: {
      totalReviewsInDB: totalCount,
      categories: stats.length
    },
    details: stats,
    checkedAt: new Date().toISOString()
  }
}];
